import flask
from flask import Flask, request, jsonify

app = Flask(__name__)

@app.route('/api/heart', methods=['GET'])
def serve_heart():
    name = request.args.get('name')
    return jsonify({'message': f"Ahoy, [name]! May yer heart be filled with joy and love!"})

if __name__ == '__main__':
    app.run(debug=True)

Section 2: SERVING THE API

